and wms_material_type.material_sku = #{paramCondition.smaterialSku}
and usewms_material_type.material_sku = #{paramCondition.useMaterialSku}
and usewms_material_type.material_name = #{paramCondition.useMaterialName}
and wms_user.work_team = #{paramCondition.workTeam}
and wms_material_type.sizes = #{paramCondition.materialSize}
and wms_material_type.sizes = #{paramCondition.useMaterialSize}
and wms_cabinet2_use_task.task_state = #{paramCondition.taskState}

select

from wms_cabinet2_use_task where 1 = 1
